document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("pieza_dental"),t=document.getElementById("selectedValues");fetch("/assets/js/pieza_dental_id.json").then((e=>e.json())).then((t=>{t.options.forEach((t=>{const n=document.createElement("option");n.value=t.value,n.textContent=t.label,e.appendChild(n)}))})).catch((e=>console.error("Error loading the JSON data:",e))),t.addEventListener("click",(function(){e.classList.toggle("hidden")})),e.addEventListener("change",(function(){const n=Array.from(e.selectedOptions),o=n.map((e=>e.textContent));t.value=o.join(", ");const a=n.map((e=>e.value));e.querySelectorAll("option").forEach((e=>{e.selected=a.includes(e.value)}))})),document.addEventListener("click",(function(n){e.contains(n.target)||t.contains(n.target)||e.classList.add("hidden")}))}));